import astfrom utils import *import csvimport reimport sysfilepath_path_gt = 'input/gt'filepath_video_games = 'input/video_games.txt'if __name__ == '__main__':    # Read ground truth    d = read_gt(filepath_path_gt)    # print(d["Assassin's Creed IV: Black Flag"])    # Open the CSV file    with open(filepath_video_games, 'r', encoding='utf-8') as file:        # Create a CSV reader        reader = csv.DictReader(file)        maxInt = sys.maxsize        while True:            # decrease the maxInt value by factor 10            # as long as the OverflowError occurs.            try:                csv.field_size_limit(maxInt)                print("csv.field_size_limit = ", maxInt)                break            except OverflowError:                maxInt = int(maxInt / 10)        # Iterate over each row in the CSV file        for row in reader:            title = row['Title']            sections_str = row['Sections']            # read the sections_str literal to a list            sections = ast.literal_eval(sections_str)            # Print the results            print(f'Title: {title}')            print(f'Sections: {sections}')            print('---')